import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function LocationPickerModal({ initialPosition, onClose, onConfirm }) {
  const [selectedPosition, setSelectedPosition] = useState(initialPosition);

  function LocationMarker() {
    useMapEvents({
      click(e) {
        setSelectedPosition([e.latlng.lat, e.latlng.lng]);
      }
    });

    return selectedPosition === null ? null : (
      <Marker position={selectedPosition}></Marker>
    );
  }

  return (
    <div style={{
      position: 'fixed',
      top: 0, left: 0, right: 0, bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 2000
    }}>
      <div style={{
        width: '90%',
        maxWidth: '400px',
        backgroundColor: '#222',
        borderRadius: '15px',
        padding: '15px',
        color: '#fff',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
      }}>
        <h3>Select Location</h3>
        <MapContainer center={selectedPosition} zoom={14} style={{ height: '300px', width: '100%', borderRadius: '10px' }}>
          <TileLayer
            attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <LocationMarker />
        </MapContainer>
        <div style={{ marginTop: '15px', display: 'flex', justifyContent: 'space-between', width: '100%' }}>
          <button onClick={onClose} style={{
            backgroundColor: '#555',
            border: 'none',
            borderRadius: '8px',
            padding: '10px 20px',
            color: '#fff',
            cursor: 'pointer'
          }}>Cancel</button>
          <button onClick={() => onConfirm(selectedPosition)} style={{
            backgroundColor: '#4a90e2',
            border: 'none',
            borderRadius: '8px',
            padding: '10px 20px',
            color: '#fff',
            cursor: 'pointer'
          }}>Confirm</button>
        </div>
      </div>
    </div>
  );
}

export default LocationPickerModal;
